import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom'

class App extends Component {
  constructor(){
    super()
    this.state= {
      topCrimes:[],
      isLoaded: false
    }
  }
  componentDidMount() {
    fetch('http://nflarrest.com/api/v1/crime')
    .then((response) => {
      return response.json();
    })
    .then((result) => {
      console.log(result);
      this.setState({
        isLoaded: true,
        topCrimes: result
      });
    });

      this.fetchCrime();

  }
  render() {
    const {  isLoaded, topCrimes, crime } = this.state;
     if (!isLoaded) {
      return <div className="loading">Loading...</div>;
    } else {
      return (
        <ul>
          {topCrimes.map(item => (
            <li key={item.id}>
              {item.author}
            </li>
          ))}
        </ul>
      );
    }
  }
}
export default App;
