import React, { Component} from 'react';
import './App.css'


class Main extends Component {
  constructor(){
    super()
    this.state= {
      category:"",
      arrest_count:""
    }
  }
  componentDidMount() {
    fetch('http://nflarrest.com/api/v1/crime')
    .then((response) => {
      return response.json();
    })
    .then((result) => {
      console.log(result);
      this.setState({
        isLoaded: true,
        items: result
      });
    });


  }
  render() {
    const {  isLoaded, items } = this.state;
     if (!isLoaded) {
      return <div className="loading">Loading...</div>;
    } else {
      return (
        <ul>
          {items.map(item => (
            <li key={item.id}>
              {item.category}
              {item.arrest_count}
            </li>
          ))}
        </ul>
      );
    }
  }
}
export default Main;
